mapper.select:
select a.id as id, a.org_name, a.authentication_status, a.operator_name, a.reject_reason, a.legal_representative ,a.business_license_number, a.business_license_type, a.apply_date, a.expiry_date ,b.company_name as org_alias ,c.approve_date as audit_date,c.audit_person as auditor_name,convert(c.audit_message,signed) as reject_type from maoding_web_org_auth a INNER JOIN maoding_web_company b on (a.id=b.id) LEFT JOIN maoding_web_org_auth_audit c on (c.is_new=1 and a.id=c.org_id) where (a.deleted is null or a.deleted=0)
and a.id=#{id}
and a.authentication_status>=#{filterStatus}
and #{expiryDateBefore}>=a.apply_date
and a.apply_date>=#{applyDateAfter}
and #{expiryDateBefore}>=a.expiry_date
and (a.org_name like concat('%',#{orgNameMask},'%'))
and (b.company_name like concat('%',#{orgAliasMask},'%'))
group by a.id

select d.company_id as company_id,d.type as type,d.file_group as file_group,d.file_path as file_path from maoding_web_project_sky_drive d where d.type in (5,8,9,10) and d.status='0'
and d.company_id in
#{orgId}
